<script>
	import { selectTextOnFocus } from "$lib/selectText.js";

	let country = "D";
	let search = "";
	let data;

	const changeCountry = () => {
		const countries = ["D", "A"];
		data = undefined;
		search = "";
		let index = countries.findIndex((i) => i === country);
		if (index === countries.length - 1) {
			country = countries[0];
		} else {
			country = countries[index + 1];
		}
	};

	const getData = async () => {
		if (search === "---" || search === "") {
			alert("Please enter something into the input field.");
		} else {
			search = search.replace(" ", "");
			let res = await fetch(`/api/${country}-${search}.json`);
			data = await res.json();
		}
	};
</script>

<main>
	<form on:submit|preventDefault={getData} spellcheck="false">
		<div class="numberPlate">
			<p class="country" on:click={changeCountry}>{country}</p>
			<div class="innerPlate">
				<input
					type="text"
					style="width: 3.5ch;"
					placeholder="---"
					maxlength="3"
					bind:value={search}
					use:selectTextOnFocus
					on:input={() => {
						search = search.replace(
							/[^A-Za-z0-9\u00c4\u00e4\u00d6\u00d6\u00f6\u00dc\u00fc\s!?]/g,
							""
						);
					}}
				/>
				<input
					type="text"
					style="width: 2.5ch;"
					placeholder="AA"
					maxlength="2"
					value="AA"
					use:selectTextOnFocus
					on:input={(event) => {
						// @ts-ignore
						event.target.value = event.target.value.replace(/[^A-Za-z!?]/g, "");
					}}
				/>
				<input
					type="text"
					style="width: 4.5ch;"
					placeholder="1234"
					maxlength="4"
					value="1234"
					use:selectTextOnFocus
					on:input={(event) => {
						// @ts-ignore
						event.target.value = event.target.value.replace(/[^\d.-]/g, "");
					}}
				/>
			</div>
		</div>
		<button type="submit">Search</button>
	</form>
