<style>
	main {
		background-color: rgba(255, 255, 255, 0.7);
		border-radius: 2rem;
		margin: 1rem 20%;
	}

	.numberPlate {
		margin: 0 auto;
		margin-top: 1rem;
		background-image: url("/numberPlate.svg");
		background-size: contain;
		background-repeat: no-repeat;
		display: flex;
		width: 620px;
		height: 127px;
	}

	.numberPlate * {
		margin: 0;
		font-size: 4rem;
		font-family: "FE-Font";
		text-transform: uppercase;
	}

	.innerPlate {
		width: 100%;
		display: grid;
		grid-template-columns: 3.5fr 2fr 4.3fr;
	}

	.country {
		padding-left: 0.05rem;
		width: 64px;
		padding-top: 4.8rem;
		margin: 0;
		color: white;
		font-size: 2rem !important;
		font-family: "Franklin Gothic Medium", "Arial Narrow", Arial, sans-serif;
		cursor: pointer;
	}

	input[type="text"] {
		border-radius: 0.5rem;
		border: 2px solid gray;
		background-color: rgba(255, 255, 255, 0);
		padding: 0 10px;
		height: 60%;
		outline: none;
		text-align: center;
		margin: auto;
	}

	input[type="text"]:focus,
	input[type="text"]:hover {
		border: 2px solid black;
	}

	button {
		cursor: pointer;
		margin: 1rem 0;
		border-radius: 1rem;
		border: none;
		padding: 0.5rem 1rem;
		background-color: red;
		color: white;
		font-size: 1.5rem;
		font-weight: bold;
	}

	.sign {
		background-image: url("/sign.svg");
		background-size: contain;
		background-repeat: no-repeat;
		background-position: center;
		height: 240px;
		width: 360px;
		margin: 0 auto;
		margin-bottom: 1rem;
		display: grid;
		align-items: center;
	}

	.sign .text {
		padding: 0 1.5rem;
	}

	.sign .text p {
		font-family: "din1451";
		word-break: break-all;
		margin: 0;
	}

	.title {
		font-size: 1.8rem;
	}

	.state {
		font-size: 1.2rem;
	}

	.result svg {
		width: 60vw;
		max-width: 400px;
	}

	.red {
		fill: #ff0000;
	}

	/* height: (127px * ...) */
	/* width: (620px * ...) */

	@media only screen and (max-width: 1100px) {
		main {
			margin: 1rem 5rem;
		}
	}

	@media only screen and (max-width: 850px) {
		main {
			margin: 1rem 0.5rem;
		}
	}

	@media only screen and (max-width: 700px) {
		.numberPlate {
			height: 101.6px;
			width: 496px;
		}

		.numberPlate * {
			font-size: 3rem;
		}

		.country {
			padding-top: 3.8rem;
			font-size: 1.5rem !important;
			width: 50px;
		}

		.sign {
			width: 300px;
			height: 200px;
		}

		.sign .text {
			padding: 0 1rem;
		}

		.title {
			font-size: 1.5rem;
		}

		.state {
			font-size: 1rem;
		}
	}

	@media only screen and (max-width: 550px) {
		.numberPlate {
			height: 63.5px;
			width: 310px;
		}

		.numberPlate * {
			font-size: 2rem;
		}

		.country {
			padding-top: 2.3rem;
			font-size: 1rem !important;
			width: 30px;
		}

		input[type="text"] {
			padding: 0 5px;
		}
	}

	@media only screen and (max-width: 350px) {
		.numberPlate {
			height: 50.8px;
			width: 240px;
		}

		.numberPlate * {
			font-size: 1.5rem;
		}

		.country {
			padding-top: 1.8rem;
			font-size: 0.8rem !important;
			width: 25px;
		}
	}
